contracts/AssetTokenization.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

/**
 * @title N.O.D.E. Asset Tokenization
 * @notice Fractional NFT ownership for real-world productive assets
 * @dev Tokenizes assets (solar arrays, 3D printers, vehicles) into fractional NFTs
 */
contract AssetTokenization {
    // ============ STATE VARIABLES ============
    
    address public owner;
    uint256 public assetCounter;
    
    // Asset structure
    struct Asset {
        uint256 id;
        address creator;
        string name;
        string description;
        string assetType; // "solar", "printer", "vehicle", etc.
        uint256 totalSupply; // Total number of shares
        uint256 totalValue; // Total value in tinybars
        uint256 pricePerShare; // Price in tinybars
        uint256 sharesSold;
        uint256 revenueGenerated; // Total revenue from asset
        uint256 lastDistribution; // Last yield distribution timestamp
        AssetStatus status;
        address beneficiary; // Who receives revenue (asset operator)
        uint256 beneficiaryFee; // Fee percentage (0-100)
    }
    
    enum AssetStatus {
        Proposed,
        Funding,
        Active,
        SoldOut,
        Delisted
    }
    
    mapping(uint256 => Asset) public assets;
    
    // Share ownership
    mapping(uint256 => mapping(address => uint256)) public shares; // assetId => owner => amount
    mapping(address => uint256[]) public userAssets; // user => assetIds they own
    
    // Revenue distribution
    uint256 public constant DISTRIBUTION_INTERVAL = 30 days;
    mapping(uint256 => mapping(address => uint256)) public pendingRevenue; // assetId => owner => amount
    
    // ============ EVENTS ============
    
    event AssetCreated(
        uint256 indexed assetId,
        address indexed creator,
        string name,
        uint256 totalSupply,
        uint256 pricePerShare
    );
    
    event SharesPurchased(
        uint256 indexed assetId,
        address indexed buyer,
        uint256 shares,
        uint256 totalCost
    );
    
    event RevenueDistributed(
        uint256 indexed assetId,
        address indexed shareholder,
        uint256 amount
    );
    
    event AssetActivated(uint256 indexed assetId);
    event AssetDelisted(uint256 indexed assetId);
    
    // ============ MODIFIERS ============
    
    modifier onlyOwner() {
        require(msg.sender == owner, "Not authorized");
        _;
    }
    
    modifier validAsset(uint256 assetId) {
        require(assets[assetId].id != 0, "Asset does not exist");
        _;
    }
    
    // ============ CONSTRUCTOR ============
    
    constructor() {
        owner = msg.sender;
    }
    
    // ============ ASSET MANAGEMENT ============
    
    /**
     * @notice Create a new asset tokenization proposal
     * @param name Asset name
     * @param description Asset description
     * @param assetType Type of asset
     * @param totalSupply Number of shares to issue
     * @param totalValue Total value of asset in tinybars
     * @param beneficiary Address that will operate the asset
     * @param beneficiaryFee Fee percentage for beneficiary (0-100)
     */
    function createAsset(
        string memory name,
        string memory description,
        string memory assetType,
        uint256 totalSupply,
        uint256 totalValue,
        address beneficiary,
        uint256 beneficiaryFee
    ) external returns (uint256) {
        require(totalSupply > 0, "Invalid supply");
        require(totalValue > 0, "Invalid value");
        require(beneficiaryFee <= 20, "Beneficiary fee too high");
        require(bytes(name).length > 0, "Name required");
        
        assetCounter++;
        uint256 assetId = assetCounter;
        
        uint256 pricePerShare = totalValue / totalSupply;
        
        assets[assetId] = Asset({
            id: assetId,
            creator: msg.sender,
            name: name,
            description: description,
            assetType: assetType,
            totalSupply: totalSupply,
            totalValue: totalValue,
            pricePerShare: pricePerShare,
            sharesSold: 0,
            revenueGenerated: 0,
            lastDistribution: 0,
            status: AssetStatus.Funding,
            beneficiary: beneficiary,
            beneficiaryFee: beneficiaryFee
        });
        
        emit AssetCreated(assetId, msg.sender, name, totalSupply, pricePerShare);
        
        return assetId;
    }
    
    /**
     * @notice Purchase shares of an asset
     * @param assetId Asset ID
     * @param shareAmount Number of shares to purchase
     */
    function purchaseShares(uint256 assetId, uint256 shareAmount) external payable validAsset(assetId) {
        Asset storage asset = assets[assetId];
        require(asset.status == AssetStatus.Funding, "Asset not in funding");
        require(shareAmount > 0, "Invalid share amount");
        require(asset.sharesSold + shareAmount <= asset.totalSupply, "Not enough shares available");
        
        uint256 totalCost = asset.pricePerShare * shareAmount;
        require(msg.value >= totalCost, "Insufficient payment");
        
        // Update asset
        asset.sharesSold += shareAmount;
        
        // Update ownership
        if (shares[assetId][msg.sender] == 0) {
            // New shareholder
            userAssets[msg.sender].push(assetId);
        }
        shares[assetId][msg.sender] += shareAmount;
        
        // Check if fully funded
        if (asset.sharesSold == asset.totalSupply) {
            asset.status = AssetStatus.Active;
            asset.lastDistribution = block.timestamp;
            emit AssetActivated(assetId);
        }
        
        // Refund excess
        if (msg.value > totalCost) {
            (bool refundSuccess, ) = payable(msg.sender).call{value: msg.value - totalCost}("");
            require(refundSuccess, "Refund failed");
        }
        
        // Transfer funds to creator (would typically go to escrow until delivery)
        (bool success, ) = payable(asset.creator).call{value: totalCost}("");
        require(success, "Transfer failed");
        
        emit SharesPurchased(assetId, msg.sender, shareAmount, totalCost);
    }
    
    /**
     * @notice Record revenue generated by an asset
     * @param assetId Asset ID
     * @dev Called by beneficiary or owner when asset generates revenue
     */
    function recordRevenue(uint256 assetId) external payable validAsset(assetId) {
        Asset storage asset = assets[assetId];
        require(asset.status == AssetStatus.Active, "Asset not active");
        require(
            msg.sender == asset.beneficiary || msg.sender == owner,
            "Not authorized"
        );
        require(msg.value > 0, "No revenue sent");
        
        asset.revenueGenerated += msg.value;
        
        // Distribute revenue to shareholders
        _distributeRevenue(assetId, msg.value);
    }
    
    /**
     * @notice Internal function to distribute revenue to shareholders
     */
    function _distributeRevenue(uint256 assetId, uint256 revenueAmount) internal {
        Asset storage asset = assets[assetId];
        
        // Calculate beneficiary fee
        uint256 beneficiaryAmount = (revenueAmount * asset.beneficiaryFee) / 100;
        uint256 distributionAmount = revenueAmount - beneficiaryAmount;
        
        // Send beneficiary fee
        if (beneficiaryAmount > 0 && asset.beneficiary != address(0)) {
            (bool success, ) = payable(asset.beneficiary).call{value: beneficiaryAmount}("");
            require(success, "Beneficiary transfer failed");
        }
        
        // Calculate distribution per share (stored for future claim functionality)
        // In production, this would track per-share distribution over time
        // For now, revenue is tracked at the asset level for claiming
        
        asset.lastDistribution = block.timestamp;
    }
    
    /**
     * @notice Claim pending revenue for owned shares
     * @param assetId Asset ID
     */
    function claimRevenue(uint256 assetId) external validAsset(assetId) {
        Asset storage asset = assets[assetId];
        require(asset.status == AssetStatus.Active, "Asset not active");
        
        uint256 userShares = shares[assetId][msg.sender];
        require(userShares > 0, "No shares owned");
        
        // Get pending revenue for this shareholder
        uint256 claimable = pendingRevenue[assetId][msg.sender];
        if (claimable > 0) {
            pendingRevenue[assetId][msg.sender] = 0;
            
            (bool success, ) = payable(msg.sender).call{value: claimable}("");
            require(success, "Transfer failed");
            
            emit RevenueDistributed(assetId, msg.sender, claimable);
        }
    }
    
    /**
     * @notice Delist an asset (owner only)
     */
    function delistAsset(uint256 assetId) external onlyOwner validAsset(assetId) {
        Asset storage asset = assets[assetId];
        require(asset.status != AssetStatus.Delisted, "Already delisted");
        
        asset.status = AssetStatus.Delisted;
        emit AssetDelisted(assetId);
    }
    
    // ============ VIEW FUNCTIONS ============
    
    /**
     * @notice Get asset details
     */
    function getAsset(uint256 assetId) external view validAsset(assetId) returns (
        address creator,
        string memory name,
        string memory description,
        string memory assetType,
        uint256 totalSupply,
        uint256 sharesSold,
        uint256 pricePerShare,
        uint256 revenueGenerated,
        AssetStatus status,
        address beneficiary
    ) {
        Asset memory asset = assets[assetId];
        creator = asset.creator;
        name = asset.name;
        description = asset.description;
        assetType = asset.assetType;
        totalSupply = asset.totalSupply;
        sharesSold = asset.sharesSold;
        pricePerShare = asset.pricePerShare;
        revenueGenerated = asset.revenueGenerated;
        status = asset.status;
        beneficiary = asset.beneficiary;
    }
    
    /**
     * @notice Get user's shares in an asset
     */
    function getUserShares(uint256 assetId, address user) external view returns (uint256) {
        return shares[assetId][user];
    }
    
    /**
     * @notice Get all assets owned by a user
     */
    function getUserAssets(address user) external view returns (uint256[] memory) {
        return userAssets[user];
    }
    
    /**
     * @notice Get asset funding progress
     */
    function getFundingProgress(uint256 assetId) external view validAsset(assetId) returns (
        uint256 sold,
        uint256 total,
        uint256 progressPercent,
        uint256 remainingShares
    ) {
        Asset memory asset = assets[assetId];
        sold = asset.sharesSold;
        total = asset.totalSupply;
        progressPercent = (sold * 100) / total;
        remainingShares = total - sold;
    }
    
    receive() external payable {
        // Allow direct revenue deposits
    }
}

contracts/LendingProtocol.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

/**
 * @title N.O.D.E. Lending Protocol
 * @notice Community-powered micro-loans with revenue-share repayment model
 * @dev Adaptive lending based on community standing, no fixed monthly payments
 */
contract LendingProtocol {
    // ============ STATE VARIABLES ============
    
    address public owner;
    address public nodeVault; // Reference to N.O.D.E. Vault
    
    uint256 public constant MIN_LOAN = 10e8; // 10 HBAR minimum
    uint256 public constant MAX_LOAN = 1000000e8; // 1M HBAR maximum
    uint256 public totalLoansIssued;
    uint256 public totalLoansOutstanding;
    uint256 public defaultRate = 2; // 2% default rate
    
    // Loan structure
    struct Loan {
        uint256 id;
        address borrower;
        address lender; // Vault or individual lender
        uint256 principal; // Original loan amount
        uint256 amountPaid; // Total amount paid back
        uint256 interestRate; // Annual percentage (e.g., 6 = 6%)
        uint256 revenueShare; // Percentage of daily revenue (e.g., 5 = 5%)
        uint256 issuedAt;
        uint256 dueDate; // Optional, for fixed-term loans
        LoanStatus status;
        uint256 lastPaymentDate;
        uint256 communityScore; // Trust score from community
    }
    
    enum LoanStatus {
        Pending,
        Active,
        Repaid,
        Defaulted,
        Cancelled
    }
    
    mapping(uint256 => Loan) public loans;
    mapping(address => uint256[]) public borrowerLoans;
    mapping(address => uint256) public borrowerDefaultCount;
    
    uint256 public loanCounter;
    
    // Lender information
    mapping(address => uint256) public lenderBalance; // Total funds provided by lender
    mapping(address => uint256) public lenderReturns; // Total returns earned
    
    // ============ EVENTS ============
    
    event LoanRequested(
        uint256 indexed loanId,
        address indexed borrower,
        uint256 amount,
        uint256 revenueShare,
        uint256 communityScore
    );
    
    event LoanIssued(
        uint256 indexed loanId,
        address indexed borrower,
        address indexed lender,
        uint256 principal,
        uint256 interestRate
    );
    
    event PaymentMade(
        uint256 indexed loanId,
        address indexed borrower,
        uint256 amount,
        uint256 remainingBalance
    );
    
    event LoanRepaid(uint256 indexed loanId, address indexed borrower);
    event LoanDefaulted(uint256 indexed loanId, address indexed borrower);
    
    // ============ MODIFIERS ============
    
    modifier onlyOwner() {
        require(msg.sender == owner, "Not authorized");
        _;
    }
    
    modifier validLoan(uint256 loanId) {
        require(loans[loanId].id != 0, "Loan does not exist");
        _;
    }
    
    // ============ CONSTRUCTOR ============
    
    constructor(address _nodeVault) {
        owner = msg.sender;
        nodeVault = _nodeVault;
    }
    
    // ============ LOAN FUNCTIONS ============
    
    /**
     * @notice Request a loan from the protocol
     * @param amount Loan amount in tinybars
     * @param revenueShare Percentage of daily revenue to repay (0-100)
     * @param communityScore Borrower's community trust score
     */
    function requestLoan(
        uint256 amount,
        uint256 revenueShare,
        uint256 communityScore
    ) external returns (uint256) {
        require(amount >= MIN_LOAN && amount <= MAX_LOAN, "Invalid loan amount");
        require(revenueShare > 0 && revenueShare <= 20, "Revenue share must be 1-20%");
        require(communityScore >= 0 && communityScore <= 100, "Invalid community score");
        
        // Calculate interest rate based on community score and default history
        uint256 baseRate = 6; // Base 6% APR
        uint256 defaultPenalty = borrowerDefaultCount[msg.sender] * 2; // +2% per default
        uint256 scoreDiscount = (100 - communityScore) / 10; // Lower score = higher rate
        
        uint256 interestRate = baseRate + defaultPenalty + scoreDiscount;
        if (interestRate > 15) interestRate = 15; // Cap at 15%
        
        loanCounter++;
        uint256 loanId = loanCounter;
        
        loans[loanId] = Loan({
            id: loanId,
            borrower: msg.sender,
            lender: address(0),
            principal: amount,
            amountPaid: 0,
            interestRate: interestRate,
            revenueShare: revenueShare,
            issuedAt: 0,
            dueDate: 0,
            status: LoanStatus.Pending,
            lastPaymentDate: 0,
            communityScore: communityScore
        });
        
        borrowerLoans[msg.sender].push(loanId);
        totalLoansIssued++;
        
        emit LoanRequested(loanId, msg.sender, amount, revenueShare, communityScore);
        
        return loanId;
    }
    
    /**
     * @notice Issue a pending loan (from vault or individual lender)
     * @param loanId Loan ID to issue
     */
    function issueLoan(uint256 loanId) external payable validLoan(loanId) {
        Loan storage loan = loans[loanId];
        require(loan.status == LoanStatus.Pending, "Loan not pending");
        require(msg.value >= loan.principal, "Insufficient funds");
        
        loan.status = LoanStatus.Active;
        loan.issuedAt = block.timestamp;
        loan.lastPaymentDate = block.timestamp;
        loan.lender = msg.sender;
        
        lenderBalance[msg.sender] += loan.principal;
        totalLoansOutstanding += loan.principal;
        
        // Transfer funds to borrower
        (bool success, ) = payable(loan.borrower).call{value: loan.principal}("");
        require(success, "Transfer failed");
        
        emit LoanIssued(loanId, loan.borrower, msg.sender, loan.principal, loan.interestRate);
    }
    
    /**
     * @notice Make a revenue-share payment on a loan
     * @param loanId Loan ID
     * @param dailyRevenue Borrower's daily revenue (used to calculate payment)
     */
    function makeRevenueSharePayment(uint256 loanId, uint256 dailyRevenue) external payable validLoan(loanId) {
        Loan storage loan = loans[loanId];
        require(loan.status == LoanStatus.Active, "Loan not active");
        require(msg.sender == loan.borrower, "Only borrower can pay");
        
        // Calculate payment based on revenue share percentage
        uint256 payment = (dailyRevenue * loan.revenueShare) / 100;
        
        // Ensure payment is sent with the transaction
        require(msg.value >= payment, "Insufficient payment");
        
        // Calculate total owed (principal + interest)
        uint256 timeElapsed = block.timestamp - loan.issuedAt;
        uint256 interestAccrued = (loan.principal * loan.interestRate * timeElapsed) / (100 * 365 days);
        uint256 totalOwed = loan.principal + interestAccrued;
        
        uint256 remainingBalance = totalOwed > loan.amountPaid ? totalOwed - loan.amountPaid : 0;
        
        if (payment > remainingBalance) {
            payment = remainingBalance;
        }
        
        loan.amountPaid += payment;
        loan.lastPaymentDate = block.timestamp;
        
        // Calculate lender return (payment minus protocol fee)
        uint256 protocolFee = payment * 1 / 100; // 1% protocol fee
        uint256 lenderReturn = payment - protocolFee;
        
        // Distribute to lender
        if (loan.lender != address(0)) {
            lenderReturns[loan.lender] += lenderReturn;
            (bool success, ) = payable(loan.lender).call{value: lenderReturn}("");
            require(success, "Lender transfer failed");
        }
        
        // Refund excess if any
        if (msg.value > payment) {
            (bool refundSuccess, ) = payable(msg.sender).call{value: msg.value - payment}("");
            require(refundSuccess, "Refund failed");
        }
        
        // Check if loan is fully repaid
        if (loan.amountPaid >= totalOwed) {
            loan.status = LoanStatus.Repaid;
            totalLoansOutstanding -= loan.principal;
            emit LoanRepaid(loanId, loan.borrower);
        }
        
        emit PaymentMade(loanId, loan.borrower, payment, remainingBalance - payment);
    }
    
    /**
     * @notice Make a direct payment (not revenue-share based)
     */
    function makeDirectPayment(uint256 loanId) external payable validLoan(loanId) {
        Loan storage loan = loans[loanId];
        require(loan.status == LoanStatus.Active, "Loan not active");
        require(msg.value > 0, "No payment sent");
        
        uint256 timeElapsed = block.timestamp - loan.issuedAt;
        uint256 interestAccrued = (loan.principal * loan.interestRate * timeElapsed) / (100 * 365 days);
        uint256 totalOwed = loan.principal + interestAccrued;
        uint256 remainingBalance = totalOwed > loan.amountPaid ? totalOwed - loan.amountPaid : 0;
        
        uint256 payment = msg.value > remainingBalance ? remainingBalance : msg.value;
        
        loan.amountPaid += payment;
        loan.lastPaymentDate = block.timestamp;
        
        // Calculate lender return
        uint256 protocolFee = payment * 1 / 100;
        uint256 lenderReturn = payment - protocolFee;
        
        if (loan.lender != address(0)) {
            lenderReturns[loan.lender] += lenderReturn;
            (bool success, ) = payable(loan.lender).call{value: lenderReturn}("");
            require(success, "Lender transfer failed");
        }
        
        if (loan.amountPaid >= totalOwed) {
            loan.status = LoanStatus.Repaid;
            totalLoansOutstanding -= loan.principal;
            emit LoanRepaid(loanId, loan.borrower);
        }
        
        emit PaymentMade(loanId, loan.borrower, payment, remainingBalance - payment);
    }
    
    /**
     * @notice Mark a loan as defaulted (called after grace period)
     */
    function markAsDefaulted(uint256 loanId) external onlyOwner validLoan(loanId) {
        Loan storage loan = loans[loanId];
        require(loan.status == LoanStatus.Active, "Loan not active");
        
        // Check if payment is overdue (e.g., 90 days without payment)
        require(
            block.timestamp > loan.lastPaymentDate + 90 days,
            "Loan not yet defaultable"
        );
        
        loan.status = LoanStatus.Defaulted;
        borrowerDefaultCount[loan.borrower]++;
        totalLoansOutstanding -= loan.principal;
        
        emit LoanDefaulted(loanId, loan.borrower);
    }
    
    // ============ VIEW FUNCTIONS ============
    
    /**
     * @notice Get loan details
     */
    function getLoan(uint256 loanId) external view validLoan(loanId) returns (
        address borrower,
        address lender,
        uint256 principal,
        uint256 amountPaid,
        uint256 interestRate,
        uint256 revenueShare,
        LoanStatus status,
        uint256 remainingBalance
    ) {
        Loan memory loan = loans[loanId];
        borrower = loan.borrower;
        lender = loan.lender;
        principal = loan.principal;
        amountPaid = loan.amountPaid;
        interestRate = loan.interestRate;
        revenueShare = loan.revenueShare;
        status = loan.status;
        
        if (loan.status == LoanStatus.Active) {
            uint256 timeElapsed = block.timestamp - loan.issuedAt;
            uint256 interestAccrued = (loan.principal * loan.interestRate * timeElapsed) / (100 * 365 days);
            uint256 totalOwed = loan.principal + interestAccrued;
            remainingBalance = totalOwed > loan.amountPaid ? totalOwed - loan.amountPaid : 0;
        } else {
            remainingBalance = 0;
        }
    }
    
    /**
     * @notice Get all loans for a borrower
     */
    function getBorrowerLoans(address borrower) external view returns (uint256[] memory) {
        return borrowerLoans[borrower];
    }
    
    /**
     * @notice Get protocol statistics
     */
    function getProtocolStats() external view returns (
        uint256 _totalLoansIssued,
        uint256 _totalLoansOutstanding,
        uint256 _defaultRate,
        uint256 _totalLenderBalance,
        uint256 _totalLenderReturns
    ) {
        _totalLoansIssued = totalLoansIssued;
        _totalLoansOutstanding = totalLoansOutstanding;
        _defaultRate = defaultRate;
        _totalLenderBalance = address(this).balance;
        _totalLenderReturns = 0; // Would need to track this
    }
    
    // ============ ADMIN FUNCTIONS ============
    
    function setDefaultRate(uint256 newRate) external onlyOwner {
        require(newRate <= 10, "Rate too high");
        defaultRate = newRate;
    }
    
    receive() external payable {
        // Allow direct deposits for lending
    }
}

contracts/NodeGovernance.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

/**
 * @title N.O.D.E. Governance
 * @notice DAO governance contract for community decision-making
 * @dev Voting mechanism for protocol changes and proposals
 */
contract NodeGovernance {
    // ============ STATE VARIABLES ============
    
    address public owner;
    uint256 public proposalCounter;
    uint256 public constant VOTING_PERIOD = 7 days;
    uint256 public constant MIN_VOTING_POWER = 1e8; // 1 HBAR staked minimum
    uint256 public quorumThreshold = 10; // 10% of total stake required
    
    // Proposal structure
    struct Proposal {
        uint256 id;
        address proposer;
        string title;
        string description;
        uint256 forVotes;
        uint256 againstVotes;
        uint256 abstainVotes;
        uint256 startTime;
        uint256 endTime;
        ProposalStatus status;
        bytes callData; // For executable proposals
        address targetContract; // Contract to call if proposal passes
        uint256 executionThreshold; // Minimum votes needed for execution
    }
    
    enum ProposalStatus {
        Pending,
        Active,
        Succeeded,
        Defeated,
        Executed,
        Cancelled
    }
    
    mapping(uint256 => Proposal) public proposals;
    mapping(uint256 => mapping(address => bool)) public hasVoted;
    mapping(uint256 => mapping(address => Vote)) public votes; // proposalId => voter => vote
    
    enum Vote {
        None,
        For,
        Against,
        Abstain
    }
    
    // Voting power from staking
    address public nodeVault;
    mapping(address => uint256) public votingPower; // Based on staked amount
    uint256 public totalVotingPower;
    
    // ============ EVENTS ============
    
    event ProposalCreated(
        uint256 indexed proposalId,
        address indexed proposer,
        string title,
        uint256 startTime,
        uint256 endTime
    );
    
    event VoteCast(
        uint256 indexed proposalId,
        address indexed voter,
        Vote vote,
        uint256 votingPower
    );
    
    event ProposalExecuted(uint256 indexed proposalId);
    event ProposalCancelled(uint256 indexed proposalId);
    event VotingPowerUpdated(address indexed voter, uint256 newPower);
    
    // ============ MODIFIERS ============
    
    modifier onlyOwner() {
        require(msg.sender == owner, "Not authorized");
        _;
    }
    
    modifier validProposal(uint256 proposalId) {
        require(proposals[proposalId].id != 0, "Proposal does not exist");
        _;
    }
    
    // ============ CONSTRUCTOR ============
    
    constructor(address _nodeVault) {
        owner = msg.sender;
        nodeVault = _nodeVault;
    }
    
    // ============ PROPOSAL FUNCTIONS ============
    
    /**
     * @notice Create a new governance proposal
     * @param title Proposal title
     * @param description Detailed description
     * @param targetContract Address of contract to call if passed (optional)
     * @param callData Function call data (optional)
     */
    function createProposal(
        string memory title,
        string memory description,
        address targetContract,
        bytes memory callData
    ) external returns (uint256) {
        require(votingPower[msg.sender] >= MIN_VOTING_POWER, "Insufficient voting power");
        require(bytes(title).length > 0, "Title required");
        require(bytes(description).length > 0, "Description required");
        
        proposalCounter++;
        uint256 proposalId = proposalCounter;
        
        proposals[proposalId] = Proposal({
            id: proposalId,
            proposer: msg.sender,
            title: title,
            description: description,
            forVotes: 0,
            againstVotes: 0,
            abstainVotes: 0,
            startTime: block.timestamp,
            endTime: block.timestamp + VOTING_PERIOD,
            status: ProposalStatus.Active,
            callData: callData,
            targetContract: targetContract,
            executionThreshold: (totalVotingPower * quorumThreshold) / 100
        });
        
        emit ProposalCreated(proposalId, msg.sender, title, block.timestamp, proposals[proposalId].endTime);
        
        return proposalId;
    }
    
    /**
     * @notice Vote on a proposal
     * @param proposalId Proposal ID
     * @param voteChoice Vote choice (For, Against, Abstain)
     */
    function vote(uint256 proposalId, Vote voteChoice) external validProposal(proposalId) {
        Proposal storage proposal = proposals[proposalId];
        require(proposal.status == ProposalStatus.Active, "Proposal not active");
        require(block.timestamp <= proposal.endTime, "Voting period ended");
        require(!hasVoted[proposalId][msg.sender], "Already voted");
        require(voteChoice != Vote.None, "Invalid vote");
        
        uint256 power = votingPower[msg.sender];
        require(power > 0, "No voting power");
        
        hasVoted[proposalId][msg.sender] = true;
        votes[proposalId][msg.sender] = voteChoice;
        
        if (voteChoice == Vote.For) {
            proposal.forVotes += power;
        } else if (voteChoice == Vote.Against) {
            proposal.againstVotes += power;
        } else if (voteChoice == Vote.Abstain) {
            proposal.abstainVotes += power;
        }
        
        emit VoteCast(proposalId, msg.sender, voteChoice, power);
    }
    
    /**
     * @notice Execute a successful proposal
     */
    function executeProposal(uint256 proposalId) external validProposal(proposalId) {
        Proposal storage proposal = proposals[proposalId];
        require(proposal.status == ProposalStatus.Active, "Proposal not active");
        require(block.timestamp > proposal.endTime, "Voting still active");
        
        // Check if proposal succeeded
        uint256 totalVotes = proposal.forVotes + proposal.againstVotes + proposal.abstainVotes;
        bool succeeded = proposal.forVotes > proposal.againstVotes && 
                         totalVotes >= proposal.executionThreshold;
        
        if (succeeded) {
            proposal.status = ProposalStatus.Succeeded;
            
            // Execute proposal if it has a target contract
            if (proposal.targetContract != address(0) && proposal.callData.length > 0) {
                (bool success, ) = proposal.targetContract.call(proposal.callData);
                require(success, "Proposal execution failed");
                proposal.status = ProposalStatus.Executed;
            }
            
            emit ProposalExecuted(proposalId);
        } else {
            proposal.status = ProposalStatus.Defeated;
        }
    }
    
    /**
     * @notice Cancel a proposal (only proposer or owner)
     */
    function cancelProposal(uint256 proposalId) external validProposal(proposalId) {
        Proposal storage proposal = proposals[proposalId];
        require(
            msg.sender == proposal.proposer || msg.sender == owner,
            "Not authorized"
        );
        require(proposal.status == ProposalStatus.Active, "Proposal not active");
        
        proposal.status = ProposalStatus.Cancelled;
        emit ProposalCancelled(proposalId);
    }
    
    // ============ VOTING POWER MANAGEMENT ============
    
    /**
     * @notice Update voting power for an address (called by vault when staking)
     * @dev This should be called by the NodeVault contract when users stake/unstake
     */
    function updateVotingPower(address voter, uint256 stakedAmount) external {
        require(msg.sender == nodeVault || msg.sender == owner, "Not authorized");
        
        uint256 oldPower = votingPower[voter];
        votingPower[voter] = stakedAmount;
        
        totalVotingPower = totalVotingPower - oldPower + stakedAmount;
        
        emit VotingPowerUpdated(voter, stakedAmount);
    }
    
    /**
     * @notice Get voting power for an address
     */
    function getVotingPower(address voter) external view returns (uint256) {
        return votingPower[voter];
    }
    
    // ============ VIEW FUNCTIONS ============
    
    /**
     * @notice Get proposal details
     */
    function getProposal(uint256 proposalId) external view validProposal(proposalId) returns (
        address proposer,
        string memory title,
        string memory description,
        uint256 forVotes,
        uint256 againstVotes,
        uint256 abstainVotes,
        uint256 startTime,
        uint256 endTime,
        ProposalStatus status,
        bool canExecute
    ) {
        Proposal memory proposal = proposals[proposalId];
        proposer = proposal.proposer;
        title = proposal.title;
        description = proposal.description;
        forVotes = proposal.forVotes;
        againstVotes = proposal.againstVotes;
        abstainVotes = proposal.abstainVotes;
        startTime = proposal.startTime;
        endTime = proposal.endTime;
        status = proposal.status;
        
        uint256 totalVotes = forVotes + againstVotes + abstainVotes;
        canExecute = block.timestamp > proposal.endTime &&
                     proposal.forVotes > proposal.againstVotes &&
                     totalVotes >= proposal.executionThreshold &&
                     proposal.status == ProposalStatus.Active;
    }
    
    /**
     * @notice Check if user has voted on a proposal
     */
    function hasUserVoted(uint256 proposalId, address user) external view returns (bool) {
        return hasVoted[proposalId][user];
    }
    
    /**
     * @notice Get user's vote on a proposal
     */
    function getUserVote(uint256 proposalId, address user) external view returns (Vote) {
        return votes[proposalId][user];
    }
    
    // ============ ADMIN FUNCTIONS ============
    
    function setQuorumThreshold(uint256 newThreshold) external onlyOwner {
        require(newThreshold > 0 && newThreshold <= 50, "Invalid threshold");
        quorumThreshold = newThreshold;
    }
    
    function setNodeVault(address newVault) external onlyOwner {
        nodeVault = newVault;
    }
}

contracts/NodeVault.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

/**
 * @title N.O.D.E. Vault
 * @notice Community-powered liquidity pool for staking HBAR with yield distribution
 * @dev Built for Hedera Hashgraph
 */
contract NodeVault {
    // ============ STATE VARIABLES ============
    
    address public owner;
    uint256 public totalStaked;
    uint256 public totalYieldGenerated;
    uint256 public constant MIN_STAKE = 1e8; // 1 HBAR (in tinybars, 1 HBAR = 100,000,000 tinybars)
    uint256 public constant YIELD_RATE = 5; // 5% annual yield
    
    // Staker information
    struct Staker {
        uint256 amount;           // Amount staked
        uint256 timestamp;        // When they staked
        uint256 lastClaim;        // Last yield claim timestamp
        uint256 pendingYield;    // Accumulated yield not yet claimed
    }
    
    mapping(address => Staker) public stakers;
    address[] public stakerAddresses;
    
    // Yield distribution pools
    struct Pool {
        string name;
        uint256 allocation;       // Percentage of yield (0-100)
        uint256 totalInvested;     // Total HBAR invested in this pool
        bool active;
    }
    
    mapping(uint256 => Pool) public pools;
    uint256 public poolCount;
    
    // ============ EVENTS ============
    
    event Staked(address indexed staker, uint256 amount, uint256 timestamp);
    event Unstaked(address indexed staker, uint256 amount, uint256 timestamp);
    event YieldClaimed(address indexed staker, uint256 yieldAmount);
    event YieldDistributed(uint256 totalYield, uint256 timestamp);
    event PoolCreated(uint256 indexed poolId, string name, uint256 allocation);
    
    // ============ MODIFIERS ============
    
    modifier onlyOwner() {
        require(msg.sender == owner, "Not authorized");
        _;
    }
    
    modifier validStaker() {
        require(stakers[msg.sender].amount > 0, "Not a staker");
        _;
    }
    
    // ============ CONSTRUCTOR ============
    
    constructor() {
        owner = msg.sender;
        
        // Initialize default pools
        _createPool("Small Business Lending", 40);
        _createPool("Green Energy Projects", 35);
        _createPool("Reserves", 25);
    }
    
    // ============ CORE FUNCTIONS ============
    
    /**
     * @notice Stake HBAR into the vault
     */
    function stake() external payable {
        require(msg.value >= MIN_STAKE, "Minimum stake is 1 HBAR");
        
        if (stakers[msg.sender].amount == 0) {
            // New staker
            stakerAddresses.push(msg.sender);
            stakers[msg.sender] = Staker({
                amount: msg.value,
                timestamp: block.timestamp,
                lastClaim: block.timestamp,
                pendingYield: 0
            });
        } else {
            // Existing staker - add to existing stake
            _updateYield(msg.sender);
            stakers[msg.sender].amount += msg.value;
        }
        
        totalStaked += msg.value;
        emit Staked(msg.sender, msg.value, block.timestamp);
    }
    
    /**
     * @notice Unstake HBAR from the vault
     * @param amount Amount to unstake (in tinybars)
     */
    function unstake(uint256 amount) external validStaker {
        Staker storage staker = stakers[msg.sender];
        require(amount <= staker.amount, "Insufficient staked amount");
        
        _updateYield(msg.sender);
        
        // Claim pending yield first
        if (staker.pendingYield > 0) {
            uint256 yieldToClaim = staker.pendingYield;
            staker.pendingYield = 0;
            staker.lastClaim = block.timestamp;
            
            (bool success, ) = payable(msg.sender).call{value: yieldToClaim}("");
            require(success, "Yield transfer failed");
            emit YieldClaimed(msg.sender, yieldToClaim);
        }
        
        // Unstake
        staker.amount -= amount;
        totalStaked -= amount;
        
        if (staker.amount == 0) {
            // Remove from staker list
            _removeStaker(msg.sender);
            delete stakers[msg.sender];
        }
        
        (bool success2, ) = payable(msg.sender).call{value: amount}("");
        require(success2, "Unstake transfer failed");
        
        emit Unstaked(msg.sender, amount, block.timestamp);
    }
    
    /**
     * @notice Claim accumulated yield
     */
    function claimYield() external validStaker {
        _updateYield(msg.sender);
        
        Staker storage staker = stakers[msg.sender];
        uint256 yieldToClaim = staker.pendingYield;
        
        require(yieldToClaim > 0, "No yield to claim");
        
        staker.pendingYield = 0;
        staker.lastClaim = block.timestamp;
        
        (bool success, ) = payable(msg.sender).call{value: yieldToClaim}("");
        require(success, "Yield transfer failed");
        
        emit YieldClaimed(msg.sender, yieldToClaim);
    }
    
    /**
     * @notice Distribute yield from pool investments
     * @dev Called by owner when pools generate returns
     */
    function distributeYield() external payable {
        require(msg.value > 0, "No yield to distribute");
        require(totalStaked > 0, "No stakers");
        
        totalYieldGenerated += msg.value;
        
        // Calculate yield per staker based on their stake and time
        for (uint256 i = 0; i < stakerAddresses.length; i++) {
            address stakerAddr = stakerAddresses[i];
            if (stakers[stakerAddr].amount > 0) {
                _updateYield(stakerAddr);
                
                // Add proportional share
                uint256 share = (msg.value * stakers[stakerAddr].amount) / totalStaked;
                stakers[stakerAddr].pendingYield += share;
            }
        }
        
        emit YieldDistributed(msg.value, block.timestamp);
    }
    
    // ============ POOL MANAGEMENT ============
    
    /**
     * @notice Create a new investment pool
     */
    function createPool(string memory name, uint256 allocation) external onlyOwner {
        require(allocation <= 100, "Allocation cannot exceed 100%");
        _createPool(name, allocation);
    }
    
    function _createPool(string memory name, uint256 allocation) internal {
        pools[poolCount] = Pool({
            name: name,
            allocation: allocation,
            totalInvested: 0,
            active: true
        });
        emit PoolCreated(poolCount, name, allocation);
        poolCount++;
    }
    
    /**
     * @notice Invest vault funds into a pool
     */
    function investInPool(uint256 poolId, uint256 amount) external onlyOwner {
        require(pools[poolId].active, "Pool not active");
        require(address(this).balance >= amount, "Insufficient vault balance");
        
        pools[poolId].totalInvested += amount;
        // In production, this would transfer to actual investment contracts
    }
    
    // ============ VIEW FUNCTIONS ============
    
    /**
     * @notice Get staker information
     */
    function getStakerInfo(address stakerAddr) external view returns (
        uint256 amount,
        uint256 pendingYield,
        uint256 stakedSince,
        uint256 estimatedAnnualYield
    ) {
        Staker memory staker = stakers[stakerAddr];
        amount = staker.amount;
        pendingYield = _calculatePendingYield(staker);
        stakedSince = staker.timestamp;
        estimatedAnnualYield = (staker.amount * YIELD_RATE) / 100;
    }
    
    /**
     * @notice Get total number of stakers
     */
    function getStakerCount() external view returns (uint256) {
        return stakerAddresses.length;
    }
    
    /**
     * @notice Get vault statistics
     */
    function getVaultStats() external view returns (
        uint256 _totalStaked,
        uint256 _totalYieldGenerated,
        uint256 _stakerCount,
        uint256 _vaultBalance
    ) {
        _totalStaked = totalStaked;
        _totalYieldGenerated = totalYieldGenerated;
        _stakerCount = stakerAddresses.length;
        _vaultBalance = address(this).balance;
    }
    
    // ============ INTERNAL FUNCTIONS ============
    
    /**
     * @notice Update yield for a staker
     */
    function _updateYield(address stakerAddr) internal {
        Staker storage staker = stakers[stakerAddr];
        uint256 pending = _calculatePendingYield(staker);
        staker.pendingYield += pending;
        staker.lastClaim = block.timestamp;
    }
    
    /**
     * @notice Calculate pending yield for a staker
     */
    function _calculatePendingYield(Staker memory staker) internal view returns (uint256) {
        if (staker.amount == 0 || staker.lastClaim == 0) {
            return 0;
        }
        
        uint256 timeElapsed = block.timestamp - staker.lastClaim;
        uint256 secondsPerYear = 365 days;
        
        // Calculate yield: (amount * rate * time) / (100 * secondsPerYear)
        uint256 yield = (staker.amount * YIELD_RATE * timeElapsed) / (100 * secondsPerYear);
        
        return yield;
    }
    
    /**
     * @notice Remove a staker from the addresses array
     */
    function _removeStaker(address stakerAddr) internal {
        for (uint256 i = 0; i < stakerAddresses.length; i++) {
            if (stakerAddresses[i] == stakerAddr) {
                stakerAddresses[i] = stakerAddresses[stakerAddresses.length - 1];
                stakerAddresses.pop();
                break;
            }
        }
    }
    
    // ============ RECEIVE FUNCTION ============
    
    receive() external payable {
        // Allow direct deposits - automatically stake
        // This allows simple HBAR transfers to the contract
        if (msg.value > 0) {
            // Call stake logic directly
            if (stakers[msg.sender].amount == 0) {
                stakerAddresses.push(msg.sender);
                stakers[msg.sender] = Staker({
                    amount: msg.value,
                    timestamp: block.timestamp,
                    lastClaim: block.timestamp,
                    pendingYield: 0
                });
            } else {
                _updateYield(msg.sender);
                stakers[msg.sender].amount += msg.value;
            }
            
            totalStaked += msg.value;
            emit Staked(msg.sender, msg.value, block.timestamp);
        }
    }
}

